label("{\tiny 4}",(5,4),N);
draw((3,4)--(-1,1)--(3,1)--cycle);
draw((3,0.44)--(-1,0.44));
draw((-1,1)--(-1,0));
draw((3,1)--(3,0));
draw((-1,1)--(0.33,0.44));
draw((0.33,0.44)--(3,4),dashed);
dot((3,1));
dot((3,0.44));
dot((-1,0.44));
dot((0.33,0.44));
