real x = 7;
pair A=(0,0), B=(0,4), C=(x,4), D=(x,0);
pair[] dots = {A,B,C,D};
dot(dots);
draw(A--B--C--D--cycle);
xaxis(-2,9,Arrows(4));
yaxis(-2,7,Arrows(4));
label("$A$",A,SW);
label("$B$",B,NW);
label("$C$",C,NE);
label("$D$",D,SE);
