pair D,EE,F,P,Q,G;

G = (0,0);
D = (-1,0);
P= (0.5,0);
EE = (0,4/3);
Q = (0,-2/3);
F = 2*Q - D;
draw(P--D--EE--F--D);
draw(EE--Q);
